import React, { useRef, useState } from 'react';
import { StyleSheet, Text, View, TouchableOpacity, Animated, Button, TouchableWithoutFeedback } from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import TButton from '../components/button';
import TModal from '../components/modal';
import PlayComputerModal from '../screens/modalContent/playComputer';
import PlayHumanModal from '../screens/modalContent/playHuman';

export default function Home({ navigation }) {

  const [showModal, setShowModal] = useState(false);

  const dropAnim = useRef(new Animated.Value(100)).current;
  const fadeAnim = useRef(new Animated.Value(0)).current;

  const fadeIn = () => {
    Animated.timing(fadeAnim, {
      toValue: 1,
      duration: 1000
    }).start();
    Animated.timing(dropAnim, {
      toValue: 0,
      duration: 1000
    }).start();
  };

  const onSettingsClicked = () => {
    console.log('settings')
    navigation.navigate('Settings')
  };

  const onComputer = () => {
    setShowModal(true)
  };

  const onHuman = () => {
    // setShowModal(true)
    navigation.navigate('Game', { isOnlineGame: true })
  };

  fadeIn()

  return (
    <View style={styles.container}>
      <TModal animationType='slide' transparent={true} visible={showModal} dismiss={() => setShowModal(false)}>
        <View style={styles.modalContent}>
          <PlayComputerModal />
        </View>
      </TModal>
      <Animated.View style={[styles.header, { marginBottom: dropAnim }]}>
        <TouchableOpacity style={styles.settingsIcon} onPress={onSettingsClicked}>
          <Ionicons name="md-settings" size={36} color="white" />
        </TouchableOpacity>
        <Text style={styles.titleText}>Tici Taca Toey</Text>
      </Animated.View>
      <Animated.View style={[styles.body, { opacity: fadeAnim }]}>
        <TButton text="Play vs computer" onPress={onComputer} />
        <TButton text="Play vs Human" onPress={onHuman} />
      </Animated.View>
    </View >
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    backgroundColor: '#eee',
  },
  header: {
    flex: 2,
    borderRadius: 15,
    flexDirection: 'column',
    backgroundColor: '#46e0ff',
    justifyContent: 'center',
    elevation: 3,
    shadowColor: '#111',
    shadowOpacity: 1,
    shadowRadius: 3,
    borderColor: '#46e0ff',
  },
  body: {
    flex: 3,
    flexDirection: 'column',
    justifyContent: 'center',
  },
  titleText: {
    flex: 1,
    padding: 10,
    fontSize: 42,
    textAlign: 'center',
    fontWeight: 'bold',
    textTransform: 'capitalize',
    color: '#FFF',
  },
  settingsIcon: {
    flex: 1,
    marginHorizontal: 20,
    marginVertical: 6,
    alignSelf: 'flex-end',
    justifyContent: 'center',
    borderColor: 'black',
  },
  modalContent: {
    flexDirection: 'row',
    backgroundColor: "white",
    borderRadius: 20,
    alignItems: "center",
    justifyContent: "center",
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5
  },
});
